#include <iostream>
using namespace std;

int height;
int x = 1;
//(I know this is a very cheap solution, if any, but I am still working on fixing the code without making this string super hugr.)
string abc = "";

int main(){
    //Takes the user input for the triangle height
    cout << "Enter the height:" << endl;
    cin >> height;

    //Makes sure the user inputs a valid height.
    if(height <= 0){
        cout << "Invalid input." << endl;
    }

    else{
        //Computes and prints each line on the triangle.
        for(int i = 0; i <= height - 1; i++){
            int y = i*i;
            string line = abc.substr(y, x);
            x += 2;
            cout << line << endl;
        }
    }
}